sources.sass\\_base.scss:
html {
    font-size: 62.5%;
    box-sizing: border-box;  
}

*,
*::after,
*::before {
    box-sizing: inherit;
    margin: 0;
    padding: 0;
}

body {
    position: relative;
    font-family: $Main-Font;
    font-size: 1.8rem;
}

ul {
    list-style: none;
}

a {
    text-decoration: none;
    color: inherit;
    font-weight: 300;
}

// a:visited,
// a:hover {
    
// }

.container {
    max-width: 1440px;
    margin: 0 auto;
}

.wrapper {
    margin: 0 auto;
    width: 80%;

    @media screen and(max-width: 500px){
        width: 90%;  
    }
}

.btn {
    appearance: none;
    border: none;
    outline: none;
    border-radius: 5rem;
    color: $White;
    font-size: 1.4rem;
    font-weight: 400;
    padding: 1.2rem 2.8rem;
    background: linear-gradient(to right, $Lime-Green, $Bright-Cyan);
    cursor: pointer;
    transition: opacity .3s ease-in-out;

    &:hover {
        opacity: .7;
    }
}

.btn:hover,
.btn:visited {
    appearance: none;
    border: none;
    outline: none;
}

.overlay {
    position: absolute;
    top: 8rem;
    right: 0;
    left: 0;
    bottom: 0;
    background: linear-gradient($Dark-Blue, $White);
    visibility: hidden;
}

.visible {
    animation: fade-in .3s ease-in-out forwards;
}

.invisible {
    animation: fade-out .3s ease-in-out forwards;
}

.noscroll {
    overflow: hidden;
}

@keyframes fade-in {
    from {
        visibility: hidden;
        opacity: 0;
    }

    to {
        visibility: visible;
        opacity: 1;
    }
}

@keyframes fade-out {
    from {
        visibility: visible;
        opacity: 1;
    }

    to {
        opacity: 0;
        visibility: hidden;
    }
}

