// Primary 
$Dark-Blue: hsl(233, 26%, 24%);
$Lime-Green: hsl(136, 65%, 51%);
$Bright-Cyan: hsl(192, 70%, 51%);

// Neutral
$Grayish-Blue: hsl(233, 8%, 62%);
$Light-Grayish-Blue: hsl(220, 16%, 96%);
$Very-Light-Gray: hsl(0, 0%, 98%);
$White: hsl(0, 0%, 100%);

// Typography
$main-font-size: 18px;

$light: 300;
$regular: 400;
$bold: 700;

// Font
$Main-Font: 'Public Sans', sans-serif;

// Desktop width 1440px
$desktop: 1440px;

// Mobile width
$mobile: 375px;

sources.sass\\_navigation.scss:
.header {
    position: relative;
    height: 8rem;
    background-color: $White;

    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo__img {
    height: 2rem;
    width: 14rem;
}

.nav {
    
    &__list {
        display: flex;
        height: 100%;
    }

    &__item {
        padding: 0 1.5rem;
        font-size: 1.4rem;
        
        &:not(:last-child) {
            padding: 0 2rem;
        }
    }

    &__link {
        position: relative;
        font-size: 1.4rem;
        font-weight: 400;
        color: $Grayish-Blue;
        transition: color .3s ease-in-out;

        &::before {
            position: absolute;
            content: "";
            width: 0;
            height: 5px;
            left: 0;
            bottom: 0; 
            background: linear-gradient(to right, $Lime-Green, $Bright-Cyan);
            opacity: 0;
            transform: translateY(3.3rem);   
            transition: all .3s ease-in-out;
        }

        &:hover {
            color: $Dark-Blue;

            &::before {
                opacity: 1;
                width: 100%;
            }
        }
    }

}

.menu {
    display: none;

     > span {
        display: block;
        width: 2.4rem;
        height: 2px;
        background-color: $Dark-Blue;
        transition: all .2s;
        transform-origin: center;

        &:not(:last-child) {
            margin-bottom: 4px;
        }
    }

    &.open {
        
        span:nth-child(1) {
            transform: translateY(6px) rotate(45deg);
        }

        span:nth-child(2) {
            opacity: 0;
        }

        span:nth-child(3) {
            transform: translateY(-6px) rotate(-45deg);
        }
    }
}

.drop__menu {
    position: absolute;
    width: 100%;
    top: 100px;
    background-color: $White;
    padding: 2.4rem 2rem;
    border-radius: 8px;

    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    z-index: 10;

    &__item {
        color: $Grayish-Blue;
        font-size: 1.4rem;
        padding: 1.5rem 0;
        font-weight: 400;
        transition: color .3s ease-in-out;

        &:hover {
            color: $Dark-Blue;
        }
    }
}


@media (max-width: 1024px) {
    .header-btn {
        display: none;
    }
    .nav {
        display: none;
    }
    .menu {
        display: block;
    }
}

sources.sass\\_hero.scss:
.hero {
    position: relative;
    min-height: 60rem;
    background-color: $Very-Light-Gray;
    display: flex;
    align-items: center;


    @media screen and(max-width: 1024px) {
        &__btn {
            margin-bottom: 3rem;
        }
    }

    @media screen and(max-width: 600px) {
        min-height: 50rem;

        &__btn {
            font-size: 1.2rem;
        }
    }

    @media screen and(max-width: 375px) {
        min-height: 50rem;
    }
    
    .content {
        width: 80%;
        margin: 0 auto;

        &__text {
            width: 40%;

            @media screen and(max-width: 1024px) {
                position: absolute;
                bottom: 0;
                width: 80%;
                text-align: center;
            }

            @media screen and(max-width: 900px) {
                position: absolute;
                bottom: 0;
                width: 80%;
                text-align: center;
            }
        }

        &__title {
            color: $Dark-Blue;
            font-weight: 300;
            font-size: 5rem;
            line-height: 1.2;
            margin-bottom: 3rem;

            @media screen and(max-width: 1024px) {
                font-size: 3.6rem;
                margin-top: 2rem;
            }

            @media screen and(max-width: 900px) {
               font-size: 3.2rem;
               margin-top: 0;
            }

            @media screen and(max-width: 768px) {
                font-size: 3rem;
            }

            @media screen and(max-width: 600px) {
               font-size: 2.6rem;
               margin-bottom: 2rem;
            }

            @media screen and(max-width: 375px) {
                font-size: 2.4rem;
            }
        }
    
        &__p {
            color: $Grayish-Blue;
            margin-bottom: 3rem;
            line-height: 1.4;
            font-size: 1.8rem;

            @media screen and(max-width: 1024px) {
                font-size: 1.6rem;
            }

            @media screen and(max-width: 900px) {
                font-size: 1.5rem;
             }

             @media screen and(max-width: 768px) {
                font-size: 1.4rem;
            }

            @media screen and(max-width: 600px) {
                font-size: 1.2rem;
            }
        }
    
        &__image {
            width: 60%;
            background-image: url('../img/bg-intro-desktop.svg');
            background-position: 70px -232px;
            background-repeat: no-repeat;
            background-size: 138%;
            position: absolute;
            top: 0;
            right: 0;
            bottom: 0;

            @media screen and(max-width: 1024px) {
                height: 30rem;
                width: 100%;
                background-position: -100px -640px;
                background-size: 138%;
                background-repeat: no-repeat;
            }

            @media screen and(max-width: 900px) {
                background-size: 140%;
            }

            @media screen and(max-width: 768px) {
                background-size: 180%;
            }

            @media screen and(max-width: 600px) {
                background-size: 243%;
            }

            @media screen and(max-width: 415px) {
                background-position: bottom center;
                background-size: 170%;
            }

            @media screen and(max-width: 375px) {
                background-position: center -292px;
                background-size: 170%;
            }

        }

        &__image::after {
            content: '';
            height: 200%;
            width: 115%;
            background-image: url('../img/image-mockups.png');
            background-position: 355px -110px;
            background-repeat: no-repeat;
            background-size: 75%;
            position: absolute;
            top: 0;
            right: 0;
            bottom: 0;

            @media screen and(max-width: 1024px) {
                width: 100%;
                height: 200%;
                background-position: center 3rem;
                background-size: 28%;
                background-repeat: no-repeat;
            }

            @media screen and(max-width: 900px) {
                background-size: 32%;
            }

            @media screen and(max-width: 768px) {
                background-size: 36%;
            }

            @media screen and(max-width: 600px) {
                background-size: 38%;
            }

            @media screen and(max-width: 415px) {
                background-size: 42%;
            }

            @media screen and(max-width: 375px) {
                background-size: 80%;
                background-position: center -107px;
            }
        }
    }
}

sources.sass\\_features.scss:
.features {
    min-height: 50rem;
    background-color: $Light-Grayish-Blue;
    padding: 10rem 0;

    @media screen and(max-width: 375px){
        padding: 4rem 0;  
    }

    &__text {
        width: 60%;

        @media screen and(max-width: 1024px){
            text-align: center;
            width: 100%;
            padding: 0 4rem;
        }

        > h2 {
            font-size: 3.4rem;
            font-weight: 300;
            color: $Dark-Blue;
            margin-bottom: 3rem;

            @media screen and(max-width: 375px){
                font-size: 2.2rem;    
            }
        }

        > p {
            color: $Grayish-Blue;
            font-size: 1.8rem;
            line-height: 1.5;
            margin-bottom: 8rem;

            @media screen and(max-width: 375px){
                font-size: 1.4rem;    
            }
        }
    }

    &__box {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
        gap: 3rem;

        @media screen and(max-width: 1024px) {
           text-align: center;
        }

        @media screen and(max-width: 1024px) {
            grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
         }

        @media screen and(max-width: 600px) {
            justify-items: center;
         }

        .item {
            min-height: 200px;

            @media screen and(max-width: 1024px) {
                padding: 0 2rem;
             }

             @media screen and(max-width: 375px) {
                width: 100%;
             }
            
            img {
                margin-bottom: 3rem;
                width: 7rem;
                height: 7rem;
            }

            h3 {
                margin-bottom: 3rem;
                color: $Dark-Blue;
                font-weight: 300;
                font-size: 2.4rem;

                @media screen and(max-width: 375px){
                    font-size: 2rem;
                }
            }

            p {
                font-size: 1.5rem;
                line-height: 1.5;
                color: $Grayish-Blue;

                @media screen and(max-width: 375px){
                    font-size: 1.2rem;
                }
            }
        }

    }
}

sources.sass\\_articles.scss:
.articles {
    background-color: $Very-Light-Gray;
    min-height: 50rem;
    padding: 8rem 0;

    @media screen and(max-width: 600px) {
        padding: 4rem;
    }

    &__content {

        @media screen and(max-width: 600px) {
            text-align: center;
        }

        &__title {
            color: $Dark-Blue;
            font-size: 3rem;
            font-weight: 300;

            @media screen and(max-width: 600px) {
                font-size: 2.6rem;
            }
        }
    }

    &__box {
        margin-top: 5rem;
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;

        @media screen and(max-width: 1024px) {
            justify-content: space-around;
         }

        @media screen and(max-width: 600px) {
            justify-content: center;
        }
        
    }

    &__item {
        margin-bottom: 3rem;
        margin-right: 2.5rem;
        width: 240px;
        min-height: 300px;
        background-color: $White;
        border-radius: 5px;
        overflow: hidden;
        transition: all .3s ease-in-out;

        &:hover {
            box-shadow: 0 12px 20px rgba(0, 0, 0 , .25);
        }

        @media screen and(max-width: 1024px) {
            width: 280px;
        }

        @media screen and(max-width: 600px) {
            margin-right: 0;
            width: 100%;
            text-align: left;
        }

        .image-1 {
            width: 100%;
            height: 200px;
            background-image: url('../img/image-currency.jpg');
            background-size: cover;
            background-position: center;
        }

        .image-2 {
            width: 100%;
            height: 200px;
            background-image: url('../img/image-restaurant.jpg');
            background-size: cover;
            background-position: center;
        }

        .image-3 {
            width: 100%;
            height: 200px;
            background-image: url('../img/image-plane.jpg');
            background-size: cover;
            background-position: center;
        }

        .image-4 {
            width: 100%;
            height: 200px;
            background-image: url('../img/image-confetti.jpg');
            background-size: cover;
            background-position: center;
        }
    }

    &__text {
        padding: 2.5rem;
        font-size: 1.2rem;
        line-height: 1.4;
        color: $Grayish-Blue;

        h4 {
            padding-bottom: 1rem;
        }

        h3 {
            color: $Dark-Blue;
            padding-bottom: 1rem;
            transition: color .3s ease;

            &:hover {
                color: $Lime-Green;
                cursor: pointer;
            }
        }
    }
}

sources.sass\\_footer.scss:
.footer {
    min-height: 16rem;
    background-color: $Dark-Blue;
    color: $White;
    font-size: 1.4rem;

    display: flex;
    align-items: center;
    justify-content: center;

    @media (max-width: 1000px) {
        padding: 3rem;
    }

    &__container {
        min-height: 9rem;
        display: flex;
        justify-content: space-between;

        @media (max-width: 800px) {
            flex-direction: column;
            align-items: center;
        }
    }

    &__logo {
        height: 2rem;
        width: 14rem;
        
        @media (max-width: 800px) {
            height: 3rem;
        }
    }

    &__social-icons {

        a:not(:nth-of-type(5)) {
            margin-right: 1rem;
        }

        svg path {
            margin-right: 1rem;
            transition: fill .3s ease-in-out;
        }

        svg path:hover {
            fill: $Lime-Green;
        }
    }

    &__links-1,
    &__links-2,
    &__links-3 {
        margin-right: 5%;
        display: flex;
        flex-direction: column;
        justify-content: space-between;

        @media (max-width: 800px) {
            margin-right: 0;
            align-items: center;
        }

        > a {
            transition: color .3s ease-in-out;

            &:hover {
                color: $Lime-Green;
            }

            @media (max-width: 800px) {
                margin-bottom: 1.2rem;
            }
        }
    }

    &__links-1 {

        @media (max-width: 800px) {
            margin-bottom: 2.5rem;
        }
    }

    &__links-2,
    &__links-3 {
        justify-content: space-evenly;
    }

    &__copyrights {
        width: 40%;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        align-items: flex-end;

        @media (max-width: 800px) {
            width: auto;
            align-items: center;
            margin-top: 2.5rem;
            margin-bottom: 2rem;
        }
        
        p {
            color: $Grayish-Blue;
        }
    }

    &__btn {
        transition: all .3s ease-in-out;

        @media (max-width: 800px) {
            margin-bottom: 2rem;
        }
    }

    &__btn:hover {
        opacity: 1;
       filter: brightness(1.12);
    }

}
